using Tekla.Structures.Geometry3d;

public class Example
{

public void PrintHoleVerticesFromCubeFace()
{

    var cubeWithHoleVertices = new[]
    {
        new Vector(   0.0,    0.0,    0.0),   // 0
        new Vector( 500.0,    0.0,    0.0),   // 1
