            strip.RebarThreading.ExtraFabricationLength = 50;
            strip.Curve.AddContourPoint(new ContourPoint(new Point(0, 200, 500), null));
            strip.Curve.AddContourPoint(new ContourPoint(new Point(0,1000, 500), null));
            strip.Insert();
       }
